import Content from '@/components/Content.vue'
//存取localStorage中的数据
const store = {
    saveList(key,value){
        window.localStorage.setItem(key,JSON.stringify(value));
    },
    fetchList(key){
     return JSON.parse(window.localStorage.getItem(key))||[];
    }
}
const list = store.fetchList("storeData");

 //const list = [{
  // id: 0,
  //content:"todo1",
  // complete: true,
  // edit: false
 //},{
   //id: 1,
  // content:"todo2",
  // complete: false,
  // edit: false
 //}];
export default {
  name: 'Home',
  components: {
    Content,
  },
  data () {
    return {
      list,
      inputValue: "",
      type: "All",
      originContent: ""
    }
  },
  watch:{
     //list:function(){
     //  store.saveList("storeData",this.list);
     //}
    list:{
      handler(){
          store.saveList("storeData",this.list);
      },
      deep:true
    }
  },

  methods:{
    handleInput(){
      console.log(this.inputValue)
      //console.log(this.item.content)
        var newlist= this.list.filter(item=> {
        item.indexOf(this.inputValue)>-1})
      console.log(newlist) 
    },

    handleAddList(e){
      const last = this.list[this.list.length - 1];
      const id = last ? last.id + 1 : 1;
      if(e.target.value){
        this.list.push({
          id: id, 
          content: e.target.value, 
          complete: false,
          edit: false
        });
        this.inputValue= "";
      }else{
        alert('您未输入任何任务');
      }
    },
    handleAddBtnList(){
      const last = this.list[this.list.length - 1];
      const id = last ? last.id + 1 : 1;
      if(this.inputValue){
        this.list.push({
          id: id, 
          content: this.inputValue, 
          complete: false,
          edit: false
        });
        this.inputValue= "";
      }else{
        alert('您未输入任何任务');
      }
    },
    handleDelete (index){
      const conf = confirm("确定删除这项吗");
      if (conf){
        this.list.splice(index,1);
      }else{
        alert("删除取消");
      }
    },
    handleComplete(item){
      item.complete = !item.complete;
    },
    handleDeleteAllComplete() {
      const confDelete = confirm("确定删除所选项吗");
      if (confDelete){
        this.list = this.list.filter(item => {
          return !item.complete;
        });
      }else{
        alert("删除取消");
      }
    },
    handleCheckedAll(e){
      this.list.forEach(item => {
          item.complete = e.target.checked;
      });
    },
    handleSelectAll(){
      this.type = "All";
    },
    handleSelectComplete(){
      this.type = "Complete";
    },
    handleSelectUnComplete(){
      this.type = "UnComplete";
    },
    handleClickEdit(item){
      item.edit = !item.edit;
      this.originContent = item.content;
    },
    handleEditCancel(item){
      item.edit = false;
      item.content = this.originContent;
    },
    handleEditBlur(item){
      item.edit = false;
    },
    handleInputEdit(item, index, e){
      if (!e.target.value.length) {
        return this.list.splice(index, 1);
      }
      item.content = e.target.value;
      item.edit = false;
      item.complete = false;
    }
  },
  computed: {
    getCountComputed() {
      return this.list.filter(item => {
          return item.complete;
      }).length;
    },
    getCountAllComputed() {
      return this.list.length
    },
    getCountUnComputed() {
      return this.list.filter(item => {
          return !item.complete;
      }).length;
    },
    selectList() {
      switch (this.type){
        case "UnComplete":
          return this.list.filter(item => {
            return !item.complete;
          });
        case "Complete":
          return this.list.filter(item => {
            return item.complete;
          });
        case "All":
          return this.list;
        default:
          return this.list;
      }
    }
  },
}
